DROP DATABASE IF EXISTS hollywood;
CREATE DATABASE hollywood;
USE hollywood;

CREATE TABLE hollywood.people (
	id INTEGER PRIMARY KEY AUTO_INCREMENT,
	name VARCHAR(255) NOT NULL,
	born DATE );
	
CREATE TABLE hollywood.movies (
	id INTEGER PRIMARY KEY AUTO_INCREMENT,
	title VARCHAR(255) NOT NULL,
	released DATE NOT NULL,
	tagline VARCHAR(1024) );
	
CREATE TABLE hollywood.roles (
	id INTEGER PRIMARY KEY AUTO_INCREMENT,
	role VARCHAR(255) NOT NULL,
	movie INTEGER NOT NULL, 
	actor INTEGER NOT NULL,
	FOREIGN KEY (movie) REFERENCES movies(id), 
	FOREIGN KEY (actor) REFERENCES people(id) );
	
CREATE TABLE hollywood.reviews (
	id INTEGER PRIMARY KEY AUTO_INCREMENT,
	movie INTEGER NOT NULL,
	author INTEGER NOT NULL,
	summary VARCHAR(1024) NOT NULL,
	rating INTEGER NOT NULL,
	FOREIGN KEY (movie) REFERENCES movies(id), 
	FOREIGN KEY (author) REFERENCES people(id) );
	
CREATE TABLE hollywood.wrote (
	movie INTEGER NOT NULL,
	author INTEGER NOT NULL,
	FOREIGN KEY (movie) REFERENCES movies(id), 
	FOREIGN KEY (author) REFERENCES people(id) );

CREATE TABLE hollywood.directed (
	movie INTEGER NOT NULL,
	director INTEGER NOT NULL,
	FOREIGN KEY (movie) REFERENCES movies(id), 
	FOREIGN KEY (director) REFERENCES people(id) );
	
CREATE TABLE hollywood.produced (
	movie INTEGER NOT NULL,
	producer INTEGER NOT NULL,
	FOREIGN KEY (movie) REFERENCES movies(id), 
	FOREIGN KEY (producer) REFERENCES people(id) );


